import React,{Component} from 'react';
class Exercice1 extends Component{
  
    constructor(){
        super();
        this.state={
            count:0
        }
    }
  
  
  
  
    render()
    {
  
    let count=0
    let plus=0
    let minus=0

     function Add(){
        count=count+1
        alert (count)
    }
    function Sub(){
        count=count-1
        alert (count)
    }

     return (
       
        <div>
            <h2>Exercice1</h2><br/><br/>
            <h1>Counter:  {this.state.count}</h1> 
            <button onClick={Add}>+</button>
            <button onClick={Sub}>-</button>
        </div>
    );
}
}
export default Exercice1;
