import React from 'react';
function Exercice1()
{
  
   
    
    let count=1
    let plus=0
    let minus=0

     function Add(){
        count=count+1
        alert (count)
    }
    function Sub(){
        count=count-1
        alert (count)
    }

     return (
       
        <div>
            <h2>Exercice1</h2><br/><br/>
            <h1>Counter:  {count}</h1> 
            <button onClick={Add}>+</button>
            <button onClick={Sub}>-</button>
        </div>
    );
}
export default Exercice1;
