import React from 'react';

function Exercice1(){
    
    let count=1;
    let plus=0;
    let minus=0;

     function handleClick(){
        count=count+1;
    }

     return (
       
        <div>
            <h2>Exercice1</h2><br/><br/>
            <h1>Counter:  {count}</h1> 
            <button onClick={handleClick()}>{`+( ${minus} clicks )`}</button>
            <button>{`-( ${plus} clicks)`}</button>
        </div>
    );
}

export default Exercice1
