import React from 'react';

function Exercice1(){
    
    let count=0;
    let plus=0;
    let minus=0;

     handleClick=()=>{
        count=count+1;
    }

  

    
    return (
       
        <div>
            <h2>Exercice1</h2>
            <h1>Counter:  {count}</h1>
            <button oncClick={handleClick}>{`+( ${minus} clicks )`}</button>
            <button>{`-( ${plus} clicks)`}</button>
        </div>
    );
}

export default Exercice1
