import React,{Component} from 'react';
class Exercice1 extends Component{
  
    constructor(){
        super();
        this.state={counter:0,plus:0,minius:0}
        
        this.addClickHandler= this.addClickHandler.bind(this);
        this.subClickHandler= this.subClickHandler.bind(this);
        this.plusClickHandler= this.plusClickHandler.bind(this);
    }
  
    addClickHandler(){
        this.setState(prevState=>{
            return {counter: prevState.counter + 1,plus: prevState.plus + 1}
        })
    }
    subClickHandler(){
        this.setState(prevState=>{
            return {counter: prevState.counter - 1}
        })
    }
    plusClickHandler(){
        this.setState(prevState=>{
            return {plus: prevState.plus + 1}
        })
    }
  
  
    render()
    {
     return (
       
        <div>
            <h2>Exercice1</h2><br/><br/>
            <h1>Counter:  {this.state.counter}</h1> 
            <button onClick={this.addClickHandler}>{`+${this.state.plus} clicks`}</button>
            <button onClick={this.subClickHandler}>{`-${this.state.minius} clicks`}</button>
        </div>
    );
}
}
export default Exercice1;
