import React,{Component} from 'react';
class Exercice1 extends Component{
  
    constructor(){
        super();
        this.state={
            count:0
        }
    }
  
  
  
  
    render()
    {
     return (
       
        <div>
            <h2>Exercice1</h2><br/><br/>
            <h1>Counter:  {this.state.count}</h1> 
            <button onClick={Add}>+</button>
            <button >-</button>
        </div>
    );
}
}
export default Exercice1;
